function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	else {
		jslog("inside else");
		var channel = g_form.getValue('u_channel');
		var devicecompliance = g_form.getValue('u_device_compliance');
		var dataclassification = g_form.getValue('u_data_classification');
		jslog("SS: Data Classification: " + dataclassification);
		jslog("SS: Channel: " + channel);
		jslog("SS: devicecompliance : " + devicecompliance);

		if (channel == 'u_internet_mobile'){
			if (devicecompliance == 'yes'){
				if (newValue == 'yes' && dataclassification == 'high'){
					var highbaselinereqs = 
'<H2>Identification and Authentication Controls</H2> ' +
'<ul><li>The information system implements replay-resistant authentication mechanisms for network access to privileged accounts</li>' +
'<li>The information system implements multifactor authentication for network access to privileged accounts</li></ul>' +						
'<H2>Authorization Controls</H2> ' + '<H2>Device Controls</H2>';
					g_form.setValue('u_requirements',highbaselinereqs);
				}
			}
		}
	}
   //Type appropriate comment here, and begin script below

}
